import {Component} from '@angular/core';
import {FormGroup, FormBuilder} from "@angular/forms";
import {TickerDetailResponseDto} from "../../dtos/responses/ticker-detail-response-dto";
import {TickerService} from "../../services/ticker.service";

@Component({
  selector: 'app-dashboard',
  templateUrl: './dashboard.component.html',
  styleUrls: ['./dashboard.component.css']
})
export class DashboardComponent {

  form: FormGroup = this.formBuilder.group({
    symbol: ['']
  });

  tickerDetails: TickerDetailResponseDto | null = null;

  constructor(private formBuilder: FormBuilder, private tickerService: TickerService) {
  }

  async search(){
    const symbol: string = this.form.get('symbol')?.value;

    if(symbol){

      this.tickerDetails = await this.tickerService.getTickerDetails(symbol);

    }
  }

}
<div class="container" fxLayout="column" fxLayoutAlign="center">

  <div fxLayout="row" fxLayoutAlign="center">
    <div fxFlex="50" fxFlex.xs="80">
      <form [formGroup]="form" (ngSubmit)="search()">
        <mat-form-field class="full-width" appearance="outline">
          <mat-label>Company Symbol</mat-label>
          <input type="email" matInput placeholder="Type in searched company" formControlName="symbol">
          <button type="submit" mat-icon-button matSuffix>
            <mat-icon>search</mat-icon>
          </button>
        </mat-form-field>
      </form>
    </div>

    <div fxFlex="10" fxFlex.xs="20" fxLayoutAlign="center">
      <button mat-fab color="primary" matTooltip="Go to watchlist" matTooltipPosition="right"
              matTooltipHideDelay="100000">
        <mat-icon>list</mat-icon>
      </button>
    </div>
  </div>

  <div fxLayout="row" fxLayout.xs="column" fxLayoutGap="20px" *ngIf="tickerDetails">

    <div fxFlex="40" fxFlex.xs="100">
      <app-company-card [tickerDetails]="tickerDetails"></app-company-card>
    </div>

    <div fxFlex="60" fxFlex.xs="100">
      <app-company-chart></app-company-chart>
    </div>
  </div>

</div>
